import React from 'react';
import PropTypes from 'prop-types';
import './Menu.scss';
import { Link } from "react-router-dom";
import axios from "axios";

const propTypes = {
  copy: PropTypes.shape({}),
};

const defaultProps = {
  copy: {},
};

class Menu extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      token: JSON.parse(localStorage["appState"]).user.auth_token,
      users: []
    }
  }

  componentDidMount() {
    axios
      .get(`http://localhost:8000/api/users/list?token=${this.state.token}`)
      .then(response => {
        console.log(response);
        return response;
      })
      .then(json => {
        if (json.data.success) {
          this.setState({ users: json.data.data });
          //alert("Login Successful!");
        } else alert("Login Failed!");
      })
      .catch(error => {
        alert(`An Error Occured! ${error}`);
      });
  }

  render() {
    const { copy } = this.props;

    return (
      <div className="row">
        <div className="col-md-9">
          <ul>
            <li>
              <Link to="/">{copy.menu.home}</Link>
            </li>
            <li>
              <Link to="/">{copy.menu.members}</Link>
            </li>
            <li>
              <Link to="/">{copy.menu.photos}</Link>
            </li>
            <li>
              <Link to="/">{copy.menu.pages}</Link>
            </li>
            <li>
              <Link to="/">{copy.menu.discussions}</Link>
            </li>
            <li>
              <Link to="/">{copy.menu.more}</Link>
            </li>
          </ul>
        </div>
        <div className="col-md-3">
          <h2>Welcome Home {"\u2728"}</h2>
          <ul>{this.state.users.map(user => <ol style={{padding:15,border:"1px solid #cccccc", width:250, textAlign:"left",marginBottom:15,marginLeft:"auto", marginRight:"auto"}}><p>Name: {user.name}</p><p>Email: {user.email}</p></ol>)}</ul>
          <button
        style={{ padding: 10, backgroundColor: "red", color: "white" }}
        onClick={this.props.logoutUser}
      >
        Logout{" "}
      </button>
        </div>
      </div>
    );
  }
}

Menu.propTypes = propTypes;
Menu.defaultProps = defaultProps;
export default Menu;
